startup:
export templdpath=$LD_LIBRARY_PATH;
export LD_LIBRARY_PATH=./linux64:$LD_LIBRARY_PATH;
export SteamAppId={{APP_ID}};
./valheim_server.x86_64  -nographics -batchmode -name "{{SERVER_NAME}}" -port {{PORT}} -world "{{WORLD}}" -password "{{PASSWORD}}" -public 1 -logFile /dev/stdout |
	sed -u -E '/^\(Filename:.*Line:[[:space:]]+[[:digit:]]+\)$/d' | 
	sed -u -E '/^([[:space:]]+)?$/d' &
export LD_LIBRARY_PATH=$templdpath;
function shutdown() { echo 1 > server_exit.drp; echo "Server exit signal set"; };
trap shutdown 15;
wait $!;
